import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import "./App.css";

const socket = io();

const projects = ["Remote100K", "Tradeview AI", "304 App"];

function App() {
  const [selectedProject, setSelectedProject] = useState("Remote100K");
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState({});

  useEffect(() => {
    socket.on("agentReply", (data) => {
      const { project, text } = data;
      setMessages((prev) => ({
        ...prev,
        [project]: [...(prev[project] || []), { sender: "agent", text }],
      }));
    });

    return () => {
      socket.off("agentReply");
    };
  }, []);

  const sendMessage = () => {
    if (!input.trim()) return;

    setMessages((prev) => ({
      ...prev,
      [selectedProject]: [
        ...(prev[selectedProject] || []),
        { sender: "user", text: input },
      ],
    }));

    socket.emit("userMessage", {
      project: selectedProject,
      text: input,
    });

    setInput("");
  };

  return (
    <div className="app">
      <aside className="sidebar">
        <h2>üìÅ Projects</h2>
        <ul>
          {projects.map((project) => (
            <li
              key={project}
              className={project === selectedProject ? "active" : ""}
              onClick={() => setSelectedProject(project)}
            >
              {project}
            </li>
          ))}
        </ul>
      </aside>

      <main className="chat">
        <h1>üß† Agent Chat</h1>
        <div className="messages">
          {(messages[selectedProject] || []).map((msg, i) => (
            <div key={i} className={msg.sender}>
              <strong>{msg.sender === "user" ? "You" : "Agent"}:</strong>{" "}
              {msg.text}
            </div>
          ))}
        </div>
        <div className="input-bar">
          <input
            type="text"
            placeholder="Ask your agent something..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </main>
    </div>
  );
}

export default App;
